 else if(href.indexOf("/ACLS/media/GlobalMediaLibrary/") >= 0){
 href = href.replace("/ACLS/media/GlobalMediaLibrary/", "http://documents.acls.org/");
 }
 
 $(this).attr("href", href);
 });
 
