    //featured events mobile slider
    if ($('.featured-events.mobile').length) {
        $(window).on('load resize',function() {
            var imgHeight =  $('.featured-events.mobile.slick-slider img').height();
            var finalHeight = imgHeight/2;
            if ( $(window).width() < 700 ) {
                $('.featured-events > .slick-arrow').css({'top': finalHeight });
                $('.featured-events .slick-dots').css({'top': imgHeight });
            }
            else {
                $('.featured-events > .slick-arrow').css({'top': '50%' });
            }
        });
    }
        
    // initialize featured story slider
    $('.featured-slider').slick();    
