    // Multi image caption 
    $('.vm-multi-image .img').click(function() {
        $(this).toggleClass('show-caption');
        $(this).siblings().removeClass('show-caption');
    });
        
    // Multi image scroll
    $(window).on('load', function(){
        $('.vm-multi-image .wrap').each(function(){
            var figure = $(this).children('figure');
            var width = parseInt($(this).css("padding-left"));
            $(figure).each(function() {
                width = width + $(this).width();
            });
            if (Modernizr.mq('(min-width: 1240px)')) {
                width = width + 30;
            }
            else if (Modernizr.mq('(min-width: 1000px)')){
                width = width + 50
            }
            else if (Modernizr.mq('(min-width: 700px)')){
                width = width + 94
            }
            else {
               width = width + 42  
            }
            var goRight = $(this).parent().find('.go-right');
            var goLeft = $(this).parent().find('.go-left');
            var scrollwidth = Math.floor(width - $(window).width());
            $(this).scroll(function() {
                if ( $(this).scrollLeft() >= (scrollwidth - 1 ) ) {
                    $(goRight).fadeOut(500);
                }
                else {
                    $(goRight).fadeIn(500);
                }
                if ( $(this).scrollLeft() == 0 ) {
                    $(goLeft).fadeOut(500);   
                }
                else {
                    $(goLeft).fadeIn(500);   
                }
            });
           
            $(goRight).click(function(e){
                e.preventDefault();
                $(this).prev().animate({ scrollLeft: scrollwidth }, 300); 
            });
            $(goLeft).click(function(e){
                e.preventDefault();
                $(this).next().animate({ scrollLeft: 0 }, 300); 
            });
        });
    });
        
