(function($){
  "use strict";
  $(document).ready(function(){
    
    $('.tour-slider').on('destroy', function (ev, slick) {
      var carousel = $(this),
        reinit = function () {
          /*
           * slick.activeBreakpoint will tell us the breakpoint
           * at which the carousel was destroyed.
           */
          if (slick.activeBreakpoint < window.innerWidth) {
            return;
          }
          
          // Re-initialize with the old settings.
          carousel.slick(slick.options);

          // Remove this event listener.
          window.removeEventListener('resize', reinit);
        };

        // Assign our callback to window.resize.
        window.addEventListener('resize', reinit);
    }).slick({
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      centerMode: true,
      centerPadding: '40px',
      mobileFirst: true,
      responsive: [
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 1
          }
        },
        {
          breakpoint: 900,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 1
          }
        },
        {
          breakpoint: 1200,
          settings: "unslick"
        }
      ]
    });
		
		/* Title Slide Bind
    ---------------------------------------------------------------*/
    var visual_tour_title_bind = 'small';
    jQuery(window).bind('resize', function(){

      if(Modernizr.mq('( min-width: 1200px )')) {
        if (visual_tour_title_bind !== 'large') {
          visual_tour_title_bind = 'large';
          jQuery(".title").insertAfter(jQuery(".grid__item.first"));
        }
      }

      else {
        if (visual_tour_title_bind !== 'small') {
          visual_tour_title_bind = 'small';
          jQuery(".title").insertBefore(jQuery(".visual-tour--wrapper"));
        }
      }
    }).resize(); 
    
  });
    
})(jQuery);
    


