/* ===== CUSTOM CSS ===== */
figure{margin: 0;}
select{max-width: 100%;}
li {line-height: inherit;}
blockquote{border-left: 0;}/*removing Bootstrap&acirc;&#128;&#153;s left border on <blockquote>*/
ul.album-list{margin: 0;} /* --- removing margin from UL around media gallery items --- */
.content .flexslider{-webkit-box-shadow: none;-moz-box-shadow: none;-o-box-shadow: none;box-shadow: none;border: 0;-webkit-border-radius: 0;-o-border-radius: 0;border-radius: 0;background: none;}/*removing carousel default styles*/
.carousel.gallery-style.flexslider{height: auto !important} /* --- fixing height issue on sidebar gallery --- */
.carousel.gallery-style.flexslider .flex-viewport{border: 0 !important} /* --- removing default border around image in Sidebar Galleries --- */
.carousel .slides, .carousel .flex-direction-nav{list-style: none;} /* --- removing bullets from showing when carousel loads --- */
.background-carousel .details {opacity: 0}/*hiding carousel details until they transition in, so you don&acirc;&#128;&#153;t see them on page load*/
.carousel figure{min-height: 0 !important;} /* --- min-height being set by default in the carousel code - causes extra space below banner photos on mobile --- */
.carousel .flex-direction-nav .flex-disabled{opacity: 0 !important;}/*not showing the back/next arrow when the carousel has nothing to rotate and is disabled*/
.background-carousel .detail-container .details{position: absolute; z-index: -1;}/*setting carousel details to absolute to prevent horizontal scroll bar caused by other details stacking up to the right of the current details container*/
.background-carousel .detail-container .details.current-details{position: relative; z-index:1;}/*setting the current details container back to position: relative - it&acirc;&#128;&#153;s default style*/
.background-carousel .crl-details:after{content: " "; display: table; clear: both;}/*fixing collapsed details container, that causes some content within the details container to extend out of the details box.*/
.mini-sitemap ul.collapsed{height: auto;}
.mini-sitemap .width-7{width: 13%; margin-right: 1%;}
.mini-sitemap .width-8{width: 11.5%; margin-right: 1%;}
.menu-type-accordion .sub2:last-child:after,.menu-type-accordion .sub3:last-child:after{padding-bottom: 0px} /* --- extra space at bottom of accordion menu subnav --- */
.brief-description ul, .long-description ul, .caption ul{list-style: disc inside;} /* --- resetting list style within a brief or long description, caption --- */
.downloads .subcategory-group > ul{margin: 0;} /* --- removing margin from UL around download 
items --- */
.email{-ms-word-break: break-all; word-break: break-word; word-break: break-all;}/*breaking long emails so they stay within the container*/
.fund .chart.top{max-width: 100%;}/*correcting width of annual fund chart when the alignment is set to top*/
.layout {position: relative; z-index: 0;} /* keeps layout contents from appearing over important lightboxes and banners */
.carousel {position: relative; z-index: 0;} /* keeps carousel descendants from stacking independent of the carousel itself */
.mobilemenu {position: relative; z-index: 100;} /* keeps the mobile menu from appearing over the emergency bulletin */
.fixed-bulletin {position: relative; z-index: 500 !important;} /* keeps the fixed bulletin banner from appearing over the bulletin lightbox */
.facultystaffdirectory .table{width: 100%; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;}/*make the table that wraps the faculty staff directly content go the full width, so some content doesn&acirc;&#128;&#153;t wrap unnecessarily*/
.menu-type-mega {position: relative; z-index: 90;}/*fixing mega menu z-index so other subnavs can show over it on hover*/
.no-mobile .telephone, .no-mobile .telephone:hover{text-decoration: none; color: inherit; font-weight: inherit; pointer-events: none;}/*don't show telephone links on non-mobile devices*/
.flex-direction-nav .flex-disabled {opacity: 0!important; cursor:none; pointer-events:none;} /*turn off arrows if no more content in a gallery*/
.menu a, .nav-menu a{color: inherit; text-decoration: none;}/*setting menu links to inherit color from item, and removing underline*/
.text .photos.left{margin-right: 10px; margin-bottom: 10px; max-width: 100%;}/*adding space between photos and text when photos are set to left, adding fix for Firefox allowing photos to break out of parent container*/
.text .photos.right{margin-left: 10px; margin-bottom: 10px; max-width: 100%;}/*adding space between photos and text when photos are set to right, adding fix for Firefox allowing photos to break out of parent container*/
.fixed-bulletin{-webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box;}/*Adding box sizing to emergency bulletin banner, because padding causes a scroll bar*/
html.styler .externalvideo{padding: 10px;} /*Adding some clickable space around an embed item in Edit Style so you can click on it and apply a style*/
